            $('#productQuantity').attr('max', variation.quantity - 5).val(1);
            $('#ProductSku').text(variation.sku);
        }

        $(function () {
            const variations = @json($variations);

            let selectedVariation = variations.find(v => v.is_cheapest == true);
            $('#variationSelect option[value="' + selectedVariation.id + '"]').prop('selected', true);
            updateVariation(selectedVariation)

            $('#variationSelect').on('change', function (){
                const variations = @json($variations);
                let id = parseInt(this.value);
                selectedVariation = variations.find(v => v.id === id);
                updateVariation(selectedVariation)
            });
        })


        $('#description').on('click', function () {
            $('#description').addClass('active');
            $('#reviews').removeClass('active');

            $('.tab-reviews').removeClass('show active');
            $('.tab-description').addClass('show active');
        });

        $('#reviews').on('click', function () {
            $('#reviews').addClass('active');
            $('#description').removeClass('active');

            $('.tab-description').removeClass('show active');
            $('.tab-reviews').addClass('show active');
        });

        $('.replyBtn').on('click', function (){
            $('#replyOf').val($(this).data('id'))
        })

    </script>
